            description: weatherData.weather[0].description,
            temp: Math.floor(weatherData.main.temp - 273.15),
            temp_max: Math.floor(weatherData.main.temp_max - 273.15),
            temp_min: Math.floor(weatherData.main.temp_min - 273.15),
            humidity: weatherData.main.humidity, 
          };
}
const WeatherApi = async (inputCity,API_key)=>{
    const url = (`http://api.openweathermap.org/data/2.5/weather?q=${inputCity}&appid=${API_key}`)
       return await fetch(url)
        .then(response=> response.json())
            .then(data =>AllData(data))
}






