import React, {useState,useEffect} from 'react';
import {WeatherApi} from "./Api/Data.js";
import {Card, Button,InputGroup, FormControl} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [inputCity,setInputCity] = useState("");
  const [weatherData, setWeatherData] = useState({});

  const submitHandler = async (e)=>{
   e.preventDefault();
  }
  const inputHandler = (e)=>{
       setInputCity(e.target.value);
  }
  console.log(WeatherApi.name)
  const API_key = "223c292e1bb3d05f5ed0eb30034a0c0b";

  useEffect(()=>{
    WeatherApi(inputCity, API_key).then(
      (data) =>setWeatherData(data.json)
    )
  }, [])

  return (
    <div className="container" >
      <h1 className=" text-center mt-5">Weather App</h1>
      <InputGroup className="mb-3 inputGroup" style={{width:"30rem"}}  >
         <FormControl
          placeholder="City name.."
          aria-label="City name"
          aria-describedby="basic-addon2"
          onChange ={inputHandler}
          value={inputCity}
        />
        <Button type="submit" onClick={submitHandler} variant="outline-secondary" id="button-addon2">
          Search
        </Button>
      </InputGroup>


    </div>
  )
}

export default App
