const WeatherApi = async (city,apiKey)=>{
    const url = (`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`)
        await fetch(url)
        .then(response=> response.json())
            .then(data =>AllData(data))
}

const AllData =(weatherData)=>{
        return {
            name: weatherData.name,
            weather: weatherData.weather[0].main,
            description: weatherData.weather[0].description,
            temp: Math.floor(weatherData.main.temp - 273.15),
            temp_max: Math.floor(weatherData.main.temp_max - 273.15),
            temp_min: Math.floor(weatherData.main.temp_min - 273.15),
            humidity: weatherData.main.humidity, 
          };
}





export  {WeatherApi}
